mapper.update:
UPDATE category
SET category_no =#{category_no},
categorySub_no =#{categorySub_no} ,
category_name =#{category_name}
WHERE category_no = #{category_no} AND categorySub_no =
#{categorySub_no}

mapper.delete:
DELETE
FROM category
WHERE category_no = #{category_no}
AND categorySub_no = #{categorySub_no}
